import { createBottomTabNavigator } from "@react-navigation/bottom-tabs";
import { MaterialCommunityIcons, Ionicons, MaterialIcons } from "@expo/vector-icons";
import { COLORS } from "../constants";
import { Favourite, Home, Profile, Search, Dashboard } from "../screens";
import { AntDesign } from '@expo/vector-icons';
const Tab = createBottomTabNavigator();

const screenOptions={
    tabBarShowLabel: false,
    headerShown: false,
    tabBarHideOnKeyboard: true,
    tabBarStyle:{
        position: "absolute",
        bottom: 0,
        right: 0,
        left: 0,
        elevation: 0,
        height: 60,
        background: COLORS.white
    }
}

const BottomTabNavigation = () => {
  const screenWidth = Dimensions.get('window').width;

  if (screenWidth < 600) {
    return (
      <Tab.Navigator screenOptions={screenOptions}>
        <Tab.Screen
           name="Home"
           component={Home}
           options={{
            tabBarIcon: ({ focused })=>{
                return (
                    <MaterialCommunityIcons
                      name={focused ? "home-outline" : "home-outline"}
                      size={24}
                      color={COLORS.black}
                    />
                )
            }
           }}
        />
         <Tab.Screen
           name="Search"
           component={Search}
           options={{
            tabBarIcon: ({ focused })=>{
                return (
                    <Ionicons
                      name="search-sharp"
                      size={24}
                      color={COLORS.black}
                    />
                )
            }
           }}
        />

      <Tab.Screen
           name="Favourite"
           component={Favourite}
           options={{
            tabBarIcon: ({ focused })=>{
                return (
                    <Ionicons
                      name={focused ? "heart-outline" : "heart-outline"}
                      size={24}
                      color={COLORS.black}
                    />
                )
            }
           }}
        />

        <Tab.Screen
        name="Dashboard"
        component={Dashboard}
        options={{
          tabBarIcon: ({ focused }) => {
            return (

              <AntDesign name={focused ? "setting":"setting"} size={24} color="black" />
            );
          },
        }}
      />

    <Tab.Screen
           name="Profile"
           component={Profile}
           options={{
            tabBarIcon: ({ focused })=>{
                return (
                    <Ionicons
                      name={focused ? "person-outline" : "person-outline"}
                      size={24}
                      color={COLORS.black}
                    />
                )
            }
           }}
        />
    </Tab.Navigator>
    );
  } else {
    return (
      <View>
       <Text style={{
                    ...FONTS.h3,
                    marginVertical: SIZES.padding *2
                }}>The Latest and Greatest</Text>
      </View>
    );
  }
};
export default BottomTabNavigation
