import { View, Text, Image, ScrollView, FlatList, TouchableOpacity } from 'react-native'
import React from 'react'
import { SafeAreaView } from 'react-native-safe-area-context'
import { COLORS, images, SIZES, FONTS } from '../constants'
import { Feather } from "@expo/vector-icons"
import { latestList, shoesList1, shoesList2 } from '../constants/data'

const Home = ({ navigation }) => {
  return (
    <SafeAreaView style={{
        flex: 1,
        backgroundColor: 'white'
    }}>
        <View style={{
            marginHorizontal: 22,
            marginTop: 15
        }}>
            <View style={{
                flexDirection: "row",
                justifyContent: "space-between",
                alignItems: "center"
            }}>
                <Image
                    source={images.logo}
                    resizeMode="contain"
                    style={{
                        width: 58,
                        height: 22
                    }}
                />

                <View>
                    <View style={{
                        position: "absolute",
                        bottom: 16,
                        width: 16,
                        height: 16,
                        borderRadius: 8,
                        backgroundColor: 'black',
                        alignItems: "center",
                        justifyContent: "center",
                        zIndex: 999
                    }}>
                        <Text style={{
                            fontSize: 10,
                            color: 'white'
                        }}>8</Text>
                    </View>
                    <Feather
                        name="shopping-bag"
                        size={24}
                        color="black"
                    />
                </View>
            </View>

            <ScrollView>
                <View style={{
                    backgroundColor: 'gray',
                    borderRadius: 20,
                    marginTop: 20,
                    width: '100%'
                }}>

<FlatList
              horizontal={true}
              data={shoesList1}
              keyExtractor={item=>item.id}
              renderItem={
                ({ item, index })=> (
                    <Image
                      source={item.shoes}
                      resizeMode="contain"
                    />
                )
              }
            />

                    {/* FlatList for shoesList2 */}

                    <View style={{
                        marginHorizontal: 12,
                        marginVertical: 20
                    }}>
                        <Text>Made for Miles</Text>
                        <Text>
                            The perfect place to find your new favourite running shoes
                        </Text>
                        <Text>ugiuh i</Text>
                    </View>
                </View>

                <View style={{
                    marginBottom: 120
                }}>
                    <Text>The Latest and Greatest</Text>

                    <FlatList
                        // FlatList for latestList
                        renderItem={({ item }) => (
                            <View>
                                <TouchableOpacity
                                    onPress={() => navigation.navigate("Details")}
                                >
                                    <Image
                                        source={item.image}
                                        style={{
                                            height: 140,
                                            width: 140
                                        }}
                                    />
                                </TouchableOpacity>

                                {/* Rest of the view for each item */}
                            </View>
                        )}
                    />
                </View>
            </ScrollView>
        </View>
    </SafeAreaView>
  )
}

export default Home
