import { createBottomTabNavigator } from "@react-navigation/bottom-tabs";
import { MaterialCommunityIcons, Ionicons } from "@expo/vector-icons";
import { COLORS } from "../constants";
import { Favourite, Home, Search, Dashboard } from "../screens";
import { AntDesign } from '@expo/vector-icons';
const Tab = createBottomTabNavigator();

const screenOptions={
  tabBarShowLabel: false,
  headerShown: false,
  tabBarHideOnKeyboard: true,
  tabBarStyle:{
    position: "absolute",
    bottom: 0,
    right: 0,
    left: 0,
    elevation: 0,
    height: 60,
    background: COLORS.white
  }
}

const BottomTabNavigation = ({ route  }) => {
  const routeName = route.name;

  return (

    <Tab.Navigator screenOptions={screenOptions}>
       <Tab.Screen
        name="Home"
        component={Home}
        options={{
          tabBarIcon: ({ focused }) => {
            return (
              <MaterialCommunityIcons
                name={focused ? "home-outline" : "home-outline"}
                size={24}
                color={COLORS.black}
              />
            )
          }
        }}
      >
        {props => <Home {...props} routeName={routeName} />} 
      </Tab.Screen>

      <Tab.Screen
        name="Search"
        component={Search}
        options={{
          tabBarIcon: ({ focused })=>{
            return (
              <Ionicons
                name="search-sharp"
                size={24}
                color={COLORS.black}
              />
            )
          }
        }}
      />

      <Tab.Screen
        name="Favourite"
        component={Favourite}
        options={{
          tabBarIcon: ({ focused })=>{
            return (
              <Ionicons
                name={focused ? "heart-outline" : "heart-outline"}
                size={24}
                color={COLORS.black}
              />
            )
          }
        }}
      />

      <Tab.Screen
        name="Dashboard"
        component={Dashboard}
        options={{
          tabBarIcon: ({ focused }) => {
            return (
              <AntDesign name={focused ? "setting":"setting"} size={24} color="black" />
            );
          },
        }}
      />

    </Tab.Navigator>
  )
}

export default BottomTabNavigation
