import { View, Text, Image, StyleSheet } from 'react-native'
import React from 'react'
import { SafeAreaView } from 'react-native-safe-area-context'
import { COLORS, images, SIZES, FONTS } from '../constants'
import Button from '../components/Button'
import TextInput from '../components/TextInput'

const Home = ({ navigation, route }) => {
 // Extract the handleLogin function from the route parameters
 const { handleLogin } = route.params;

 // Now you can use the handleLogin function
 const onHandleLogin = (data, username) => {
     handleLogin(data, username);
 };
 return (
        <SafeAreaView style={{flex: 1, backgroundColor: COLORS.white}}>
            <View style={{marginHorizontal: 22,marginTop: 15}}>
                <View style={{flexDirection: "row", justifyContent: "space-between", alignItems: "center" }}>
                    <Image source={images.logo} resizeMode="contain" style={{ width: 88, height: 32 }}/>
                    <View>
                        <Text style={{...FONTS.h3}}>Welcome <Text style={{color:"green"}}>{message}</Text></Text>
                    </View>
                </View> 

                <View  style={{backgroundColor: COLORS.gray, borderRadius: 20, marginTop: SIZES.padding, width: SIZES.width - 44, marginTop:30 }}>
                    <View style={styles.row}>
                        <View style={[styles.col, styles.col6, styles.centerContent]}>
                            <Text style={{...FONTS.h3}}>My Wallet</Text>
                            <Text style={{...FONTS.body4, marginVertical: 10}}>Available balance: Rs. 5297.39 </Text>
                        </View>
                        <View style={[styles.col, styles.col6, styles.centerContent]}>
                            <Text style={{...FONTS.h3}}>Recharge Wallet</Text>
                            <View style={{flexDirection: 'row'}}>
                                <Button mode="contained" style={{width: 25, height: 22,marginHorizontal: 2}}>Rs.500</Button>
                                <Button mode="contained" style={{width: 25, height: 22,marginHorizontal: 2}}>Rs.1000</Button>
                                <Button mode="contained" style={{width: 25, height: 22,marginHorizontal: 2}}>Rs.2000</Button>
                            </View>
                            {/* <View style={{ flexDirection: 'row', alignItems: 'center' }}>
                                <TextInput label="Enter Amount" returnKeyType="next"  style={{ width:120, height: 22}} />
                                <Button mode="contained" style={{  width: 25, height: 22}}>Submit</Button>
                            </View> */}
                        </View>
                    </View>
                </View>

                <View>
                    <Button mode="contained"  style={{width: 100, height: 50}} onPress={() => navigation.navigate('StartScreen')}>Logout</Button>
                </View>
            </View>
        </SafeAreaView>
    )
}

    const styles = StyleSheet.create({
        container: {
        flex: 1,
        flexDirection: 'column', // Layout from top to bottom
        },
        row: {
        flexDirection: 'row', // Layout from left to right
        flexWrap: 'wrap', // Wrap items to next line if they exceed the container width
        marginHorizontal: 12, // Horizontal margin
        marginVertical: SIZES.padding, // Vertical margin
        },
        col: {
        flexGrow: 1, // Allow column to grow and occupy available space
        flexBasis: '0%', // Reset base width
        },
        col6: {
        width: '50%', // Set width to 50% for a column in a 12-column grid system
        },
        centerContent: {
        alignItems: 'center', // Center content vertically
        justifyContent: 'center', // Center content horizontally
        },
    });
export default Home
